// client/src/App.js â€” full loop UI (Tailwind + Framer Motion friendly)
import React, { useEffect, useState } from 'react';
import io from 'socket.io-client';
import { motion, AnimatePresence } from 'framer-motion';

const SERVER = process.env.REACT_APP_SERVER || 'http://localhost:4000';
const socket = io(SERVER, { transports: ['websocket'] });

export default function App() {
  const [name, setName] = useState('Player');
  const [roomId, setRoomId] = useState('');
  const [meId, setMeId] = useState(null);

  const [room, setRoom] = useState(null);              // public snapshot (players/scores/stage/round/scenario)
  const [hand, setHand] = useState([]);                // my private hand
  const [submitted, setSubmitted] = useState(false);   // did I submit a response this round?
  const [voted, setVoted] = useState(false);           // did I vote this round?
  const [options, setOptions] = useState([]);          // voting options
  const [reveal, setReveal] = useState(null);          // reveal payload
  const [chat, setChat] = useState([]);
  const [msg, setMsg] = useState('');

  useEffect(() => {
    socket.on('connect', () => setMeId(socket.id));
    socket.on('roomCreated', ({ roomId, room }) => { setRoomId(roomId); setRoom(room); });
    socket.on('roomUpdate', (r) => setRoom(r));
    socket.on('chatUpdate', (c) => setChat(c));

    socket.on('roundStarted', ({ room }) => {
      setRoom(room);
      setSubmitted(false);
      setVoted(false);
      setReveal(null);
      setOptions([]);
      setHand([]); // will get via yourHand
    });
    socket.on('yourHand', ({ hand }) => setHand(hand || []));

    socket.on('responsesUpdate', () => {
      // progress only; no UI change needed here beyond maybe a toast
    });
    socket.on('votingStarted', ({ room, options }) => {
      setRoom(room);
      setOptions(options);
    });
    socket.on('votesUpdate', () => {});
    socket.on('reveal', (payload) => {
      setReveal(payload);
      // scores already updated, room will refresh via roomUpdate soon
    });
    socket.on('gameOver', (payload) => {
      setReveal(null);
      // final results in payload
      setRoom(r => ({ ...(r||{}), stage: 'finished', final: payload }));
    });

    return () => socket.off();
  }, []);

  // ---- Lobby actions ----
  const createRoom = () => socket.emit('createRoom', { name });
  const joinRoom = () => socket.emit('joinRoom', { roomId, name });
  const startGame = () => socket.emit('startGame', { roomId });

  // ---- Chat ----
  const sendMessage = () => {
    if (!msg.trim()) return;
    socket.emit('sendChat', { roomId, message: msg.trim() });
    setMsg('');
  };

  // ---- Respond / Vote ----
  const submitResponse = (cardText) => {
    if (submitted) return;
    socket.emit('submitResponse', { roomId, cardText });
    setSubmitted(true);
  };
  const submitVote = (responseId) => {
    if (voted) return;
    socket.emit('submitVote', { roomId, responseId });
    setVoted(true);
  };

  const isHost = room?.players?.[0]?.id === meId || room?.host === meId;

  return (
    <div className="min-h-screen bg-slate-50 text-slate-900">
      <div className="max-w-6xl mx-auto p-4">
        <header className="flex items-center justify-between mb-4">
          <h1 className="text-2xl font-bold text-indigo-600">Worst-Case Scenario â€” Online</h1>
          <div className="text-sm text-slate-500">Room: <span className="font-mono">{roomId || '-'}</span></div>
        </header>

        {!room && (
          <div className="bg-white rounded-xl shadow p-4">
            <div className="flex flex-wrap gap-2 items-center">
              <input className="border rounded p-2" value={name} onChange={e=>setName(e.target.value)} placeholder="Your name" />
              <button onClick={createRoom} className="px-3 py-2 rounded bg-indigo-600 text-white hover:bg-indigo-700">Create Room</button>

              <input className="border rounded p-2 ml-4" value={roomId} onChange={e=>setRoomId(e.target.value)} placeholder="Room ID" />
              <button onClick={joinRoom} className="px-3 py-2 rounded bg-emerald-600 text-white hover:bg-emerald-700">Join Room</button>
            </div>
          </div>
        )}

        {room && (
          <div className="grid grid-cols-1 lg:grid-cols-3 gap-4">
            {/* Left: players & scores */}
            <aside className="bg-white rounded-xl shadow p-4">
              <div className="flex items-center justify-between mb-2">
                <div className="font-semibold">Players</div>
                <div className="text-sm text-slate-500">Round {room.roundIndex || 0}/{room.maxRounds || 0}</div>
              </div>
              <ul className="space-y-2">
                {room.players.map(p => (
                  <li key={p.id} className={`flex items-center justify-between rounded px-2 py-1 ${p.id===meId?'bg-indigo-50':''}`}>
                    <span className="truncate">{p.avatar} {p.name}{p.id===meId?' (you)':''}</span>
                    <span className="font-mono">{p.score}</span>
                  </li>
                ))}
              </ul>
              {room.stage === 'lobby' && isHost && (
                <button onClick={startGame} className="mt-3 w-full px-3 py-2 rounded bg-indigo-600 text-white hover:bg-indigo-700">
                  Start Game
                </button>
              )}
            </aside>

            {/* Middle: table / phases */}
            <main className="bg-white rounded-xl shadow p-4 lg:col-span-2">
              {/* Stage banner */}
              <div className="mb-3 text-sm text-slate-500">
                Stage: <span className="font-medium">{room.stage}</span>
              </div>

              {/* Scenario */}
              {(room.stage === 'respond' || room.stage === 'vote' || room.stage === 'reveal') && (
                <motion.div
                  key={room.tableScenario}
                  initial={{ opacity: 0, y: -8 }}
                  animate={{ opacity: 1, y: 0 }}
                  className="mb-4 p-4 rounded-lg bg-slate-100">
                  <div className="text-xs uppercase tracking-wider text-slate-500">Scenario</div>
                  <div className="text-lg">{room.tableScenario}</div>
                </motion.div>
              )}

              {/* RESPOND PHASE */}
              {room.stage === 'respond' && (
                <>
                  <div className="text-sm mb-2">Pick one card from your hand as your response:</div>
                  <div className="grid grid-cols-1 sm:grid-cols-3 gap-3">
                    {hand.map((c, i) => (
                      <motion.button
                        key={i}
                        whileHover={{ scale: 1.02 }} whileTap={{ scale: 0.98 }}
                        disabled={submitted}
                        onClick={() => submitResponse(c)}
                        className={`p-3 rounded-lg border text-left ${submitted?'opacity-50 cursor-not-allowed':'hover:border-indigo-400'}`}
                      >
                        {c}
                      </motion.button>
                    ))}
                  </div>
                  <div className="mt-3 text-sm text-slate-500">
                    {submitted ? 'Waiting for other playersâ€¦' : 'Choose one card to submit.'}
                  </div>
                </>
              )}

              {/* VOTING PHASE */}
              {room.stage === 'vote' && (
                <>
                  <div className="text-sm mb-2">Vote for the best response (you canâ€™t vote for yourself):</div>
                  <div className="space-y-2">
                    {options.map(opt => (
                      <motion.button
                        key={opt.responseId}
                        whileHover={{ scale: 1.01 }} whileTap={{ scale: 0.99 }}
                        disabled={voted}
                        onClick={() => submitVote(opt.responseId)}
                        className={`w-full p-3 rounded-lg border text-left ${voted?'opacity-50 cursor-not-allowed':'hover:border-emerald-400'}`}
                      >
                        {opt.text}
                      </motion.button>
                    ))}
                  </div>
                  <div className="mt-3 text-sm text-slate-500">
                    {voted ? 'Thanks! Waiting for other votesâ€¦' : 'Pick your favorite.'}
                  </div>
                </>
              )}

              {/* REVEAL */}
              {room.stage === 'reveal' && reveal && (
                <>
                  <div className="text-sm mb-2">Results:</div>
                  <div className="space-y-2">
                    <AnimatePresence>
                      {reveal.revealList.map(item => (
                        <motion.div
                          key={item.responseId}
                          initial={{ opacity: 0, x: -10 }}
                          animate={{ opacity: 1, x: 0 }}
                          exit={{ opacity: 0 }}
                          className="p-3 rounded-lg border bg-emerald-50"
                        >
                          <div className="font-medium">{item.text}</div>
                          <div className="text-sm text-slate-600">
                            by <strong>{item.authorName}</strong> â€” votes: <strong>{item.votes}</strong>
                          </div>
                        </motion.div>
                      ))}
                    </AnimatePresence>
                  </div>
                  <div className="mt-3 text-sm text-slate-500">Next round startingâ€¦</div>
                </>
              )}

              {/* FINISHED */}
              {room.stage === 'finished' && (
                <div className="text-center">
                  <h2 className="text-2xl font-bold mb-2">Game Over</h2>
                  <div className="mb-4">Winner: <span className="font-semibold">
                    {room.final?.winner?.avatar} {room.final?.winner?.name} ({room.final?.winner?.score})
                  </span></div>
                  <div className="bg-slate-100 rounded p-3 inline-block">
                    <div className="font-semibold mb-1">Final Scores</div>
                    <ul className="text-left">
                      {room.final?.players?.map(p => (
                        <li key={p.id}>{p.avatar} {p.name}: <span className="font-mono">{p.score}</span></li>
                      ))}
                    </ul>
                  </div>
                </div>
              )}
            </main>

            {/* Right: chat */}
            <aside className="bg-white rounded-xl shadow p-4">
              <div className="font-semibold mb-2">Chat</div>
              <div className="h-60 overflow-y-auto border rounded p-2 mb-2 bg-slate-50">
                {chat.map((c,i)=> (
                  <div key={i} className="text-sm">
                    <span className="mr-1">{c.avatar}</span>
                    <span className="font-medium">{c.player}:</span> {c.message}
                  </div>
                ))}
              </div>
              <div className="flex gap-2">
                <input className="border rounded p-2 flex-1" value={msg} onChange={e=>setMsg(e.target.value)} placeholder="Type a message" />
                <button onClick={sendMessage} className="px-3 py-2 rounded bg-slate-800 text-white hover:bg-slate-900">Send</button>
              </div>
            </aside>
          </div>
        )}
      </div>
    </div>
  );
}
