import React, {useState, useEffect} from 'react';
import io from 'socket.io-client';
import { motion } from 'framer-motion';
const SERVER = process.env.REACT_APP_SERVER || 'http://localhost:4000';
const socket = io(SERVER);

export default function App(){
  const [name,setName]=useState('Player'); const [roomId,setRoomId]=useState('');
  const [room,setRoom]=useState(null); const [chat,setChat]=useState([]); const [message,setMessage]=useState('');

  useEffect(()=>{
    socket.on('roomCreated',({roomId,room})=>{setRoomId(roomId); setRoom(room)});
    socket.on('roomUpdate',(r)=>setRoom(r));
    socket.on('chatUpdate',(c)=>setChat(c));
    return ()=> socket.off();
  },[]);

  const createRoom=()=>{ socket.emit('createRoom',{name}); };
  const joinRoom=()=>{ socket.emit('joinRoom',{roomId,name}); };
  const sendMessage=()=>{ socket.emit('sendChat',{roomId,message}); setMessage(''); };

  return (
    <div className='p-6 max-w-4xl mx-auto bg-gray-50 rounded-xl shadow-lg font-sans'>
      {!room && <div className='text-center'>
        <h2 className='text-3xl font-bold mb-4 text-indigo-600'>Worst-Case Scenario Game</h2>
        <input className='border p-2 mr-2 rounded' value={name} onChange={e=>setName(e.target.value)} placeholder='Your name'/>
        <button className='bg-indigo-500 hover:bg-indigo-600 text-white px-3 py-2 rounded' onClick={createRoom}>Create Room</button>
        <div className='mt-4'>
          <input className='border p-2 mr-2 rounded' value={roomId} onChange={e=>setRoomId(e.target.value)} placeholder='Room ID'/>
          <button className='bg-green-500 hover:bg-green-600 text-white px-3 py-2 rounded' onClick={joinRoom}>Join Room</button>
        </div>
      </div>}

      {room && <div>
        <h3 className='text-xl font-semibold mb-2'>Room: {room.id}</h3>
        <ul className='mb-4'>
          {room.players.map(p=> <li key={p.id}>{p.avatar} {p.name} - Score: {p.score}</li>)}
        </ul>
        <div className='border p-2 h-64 overflow-y-auto mb-2 bg-white rounded'>
          {chat.map((c,i)=><motion.div key={i} initial={{opacity:0}} animate={{opacity:1}}>{c.avatar} {c.player}: {c.message}</motion.div>)}
        </div>
        <input className='border p-2 mr-2 rounded' value={message} onChange={e=>setMessage(e.target.value)} placeholder='Message'/>
        <button className='bg-purple-500 hover:bg-purple-600 text-white px-3 py-2 rounded' onClick={sendMessage}>Send</button>
      </div>}
    </div>
  );
}
